import sympy as sp

min_val = null
for r in range(-5, 6):
    for s in range(-5, 6):
        if r == 0 or s == 0 or r == s:
            continue
        norm_sq = r**2 - r*s + s**2
        if norm_sq > 0:
            val = sp.sqrt(norm_sq)
            if min_val is null or val < min_val:
                min_val = val

result = min_val

print(result)

