rationale:
def reverse_num(n):
    return int(str(n)[::-1])

def steps_to_palindrome(n):
    steps = 0
    while true:
        if str(n) == str(n)[::-1] and steps > 0:  # if palindrome after at least one iteration
            return steps
        n = n + reverse_num(n)
        steps += 1
        if steps > 1000:  # safeguard to prevent infinite loop
            return null

pal_sum = 0
for n in range(10, 100):
    if str(n) == str(n)[::-1]:
        continue  # skip palindromes
    s = steps_to_palindrome(n)
    if s == 6:
        pal_sum += n

result = pal_sum
print(result)

