import sympy as sp

# The function f is defined as the Cantor function.
# Use the standard recursive definition:
# f(x) = 1/2 f(3*x) if 0 <= x <= 1/3,
# f(x) = 1/2 if 1/3 <= x <= 2/3,
# f(x) = 1/2 + 1/2 f(3*x - 2) if 2/3 <= x <= 1.


def f(x):
    # Use conditional recursion for rational x using float comparisons with tolerance
    if x < sp.Rational(1,3):
        return sp.Rational(1,2)*f(3*x)
    elif x <= sp.Rational(2,3):
        return sp.Rational(1,2)
    else:
        return sp.Rational(1,2) + sp.Rational(1,2)*f(3*x - 2)

result = f(sp.Rational(2,7))
result
print(result)

