import sympy as sp

# Define prime check function

def is_prime(n):
    return sp.ntheory.primetest.isprime(n)

primes = list(sp.primerange(2, 50))  # Consider primes up to 50

min_perimeter = null

# Iterate through distinct sets of 3 primes
for i in range(len(primes)):
    for j in range(i+1, len(primes)):
        for k in range(j+1, len(primes)):
            a, b, c = primes[i], primes[j], primes[k]
            # Check triangle inequality for scalene triangle
            if a + b > c and a + c > b and b + c > a:
                perimeter = a + b + c
                if is_prime(perimeter):
                    if min_perimeter is null or perimeter < min_perimeter:
                        min_perimeter = perimeter

result = min_perimeter
print(result)

import sympy as sp

# We'll define a function f(a, b) = probability to reach (0, 0) when starting at (a, b)
# with the process: if a>0 and b>0 then f(a,b) = (1/3)[f(a-1, b) + f(a, b-1) + f(a-1, b-1)]
# boundary: if a==0 and b==0: f(0,0)=1, if a==0 and b>0 or a>0 and b==0 then f=0 because hitting axis not at (0,0) 

from functools import lru_cache

@lru_cache(maxsize=null)
def f(a, b):
    if a == 0 and b == 0:
        return sp.Rational(1, 1)
    if a == 0 or b == 0:
        return sp.Rational(0, 1)
    return sp.Rational(1, 3)*(f(a-1, b) + f(a, b-1) + f(a-1, b-1))

result_fraction = sp.nsimplify(f(4,4))
result_fraction_simpl = sp.together(result_fraction)  # ensure it's a rational

# Convert denominator and numerator to get m and n
m = sp.numer(result_fraction_simpl)
n = sp.log(sp.denom(result_fraction_simpl), 3)

# We need to return m+n.
solution = m + n
result = solution
result_str = f"m = {m}, n = {n}, m + n = {solution}"
result_str
print(result)

