import sympy as sp

x = sp.symbols('x', integer=true)

expr = 6*x**2 - 47*x + 15  # The expression

solutions = []

# Solve for cases when one factor equals 1 or -1
cases = [(2*x - 15, 1), (2*x - 15, -1), (3*x - 1, 1), (3*x - 1, -1)]

for factor, value in cases:
    sols = sp.solve(sp.Eq(factor, value), x)
    for sol in sols:
        if sol.is_integer:
            val_expr = abs(expr.subs(x, sol))
            if sp.ntheory.primetest.isprime(int(val_expr)):
                solutions.append(sol)

if solutions:
    result = max(solutions)
else:
    result = null

print(result)

import sympy as sp

count = 0
for N in range(1, 1000):
    representations = 0
    # j ranges from 1 to floor(sqrt(N)) since need j <= N/j so j^2 <= N
    for j in range(1, int(sp.sqrt(N)) + 1):
        if N % j == 0:
            q = N // j
            if q >= j and (q - j) % 2 == 0:
                representations += 1
    if representations == 5:
        count += 1

result = count

print(result)

