draw((0,i)--(3,i),linewidth(0.7));
draw((i,0)--(i,3),linewidth(0.7));
}
label("1",(1.5,2),N);
label("2",(2.5,0),N);
label("3",(0.5,1),N);
label("4",(0.5,0),N);
label("10",(1.5,1),N);
label("11",(2.5,2),N);
label("12",(2.5,1),N);
label("13",(0.5,2),N);
label("14",(1.5,0),N);
[/asy]

rationale:
import sympy as sp

S, H, E = sp.symbols('S H E', integer=true, positive=true)

digits = [1, 2, 3, 4]
solution = []
for s in digits:
    for h in digits:
        for e in digits:
            if s != h and s != e and h != e:
                # addition digit by digit
                # ones place: E+E = S + 5*a, a (carry) must be 0 or 1
                # tens place: H + H + a = E + 5*b, b must be 0 because S+b = S
                # hundreds: S + b = S, so b=0
                for a in [0,1]:
                    if (2*e) == (s + 5*a):
                        if (2*h + a) == e:  
                            solution.append((s, h, e))

sol = solution[0]
S_val, H_val, E_val = sol
total = S_val + H_val + E_val

# Convert total to base 5
base5 = ''
n = total
if n == 0:
    base5 = '0'
while n > 0:
    base5 = str(n % 5) + base5
    n //= 5

result = base5
print(result)

